package com.a58program.sendsmstool.base;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.ViewGroup.LayoutParams;import android.widget.LinearLayout;import com.a58program.sendsmstool.R;//import com.umeng.analytics.MobclickAgent;public abstract class BaseFragment extends Fragment {	protected Context mContext;	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = getActivity();	}	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {		View rootView = initParent(inflater, container);		findViews(rootView);		setListensers();		return rootView;	}	@Override	public void onActivityCreated(Bundle savedInstanceState) {		super.onActivityCreated(savedInstanceState);		initVariable();	}	private View initParent(LayoutInflater inflater, ViewGroup container) {		View rootView = inflater.inflate(R.layout.fragment_base_layout, container, false);		LinearLayout subCententView = (LinearLayout) rootView.findViewById(R.id.base_sub_fragment_layout);		LayoutParams layoutParams = new LayoutParams(LayoutParams.MATCH_PARENT,				LayoutParams.MATCH_PARENT);		View centerView = View.inflate(mContext, setContentView(), null);		subCententView.addView(centerView, layoutParams);		return rootView;	}	/**	 * 跳转到某个Activity	 */	protected void gotoActivity(Context mContext, Class<?> toActivityClass, Bundle bundle) {		Intent intent = new Intent(mContext, toActivityClass);		if (bundle != null) {			intent.putExtras(bundle);		}		mContext.startActivity(intent);		((Activity) mContext).overridePendingTransition(R.anim.push_right_in, R.anim.not_exit_push_left_out);	}	public void gotoActivityForResult(Context mContext, Intent intent, int requestCode) {		super.startActivityForResult(intent, requestCode);		((Activity)mContext).overridePendingTransition(R.anim.push_right_in, R.anim.not_exit_push_left_out);	}	/**	 * 初始化子类fragment的变量	 */	protected abstract void initVariable();	/**	 * 加载子类布局	 */	protected abstract int setContentView();	/**	 * 加载控件	 */	protected abstract void findViews(View rootView);	/**	 * 设置监听	 */	protected abstract void setListensers();	@Override	public void onResume() {		super.onResume();//		MobclickAgent.onResume(mContext);	}	@Override	public void onPause() {		super.onPause();//		MobclickAgent.onPause(mContext);	}}